SELECT r.rNum, r.alarm_no, r.froM_user_no, r.to_user_no, r.alarm_content, r.alarm_date, r.sell_no, r.alarm_title, r.buy_type, r.user_no, r.buy_no, r.b_buy_no, r.booking_no FROM (SELECT ROWNUM rNum, a.alarm_no, a.from_user_no, a.to_user_no, a.alarm_content, a.alarm_date, a.sell_no, a.alarm_title, a.buy_type, a.user_no, a.buy_no, a.b_buy_no, a.booking_no FROM (SELECT a.alarm_no, a.from_user_no, a.to_user_no, a.alarm_content, a.alarm_date, a.sell_no, a.alarm_title, a.buy_type, u.user_no, b.buy_no, bb.b_buy_no, bb.booking_no FROM alarm a, users u, buy b, bbuy bb WHERE a.to_user_no = u.user_no and b.user_no = u.user_no and bb.user_no = u.user_no and to_user_no = #{user_no}
and title like '%' || #{keyword} || '%'
ORDER BY alarm_no desc)a)r WHERE r.rNum >= #{startNum} and r.rNum <= #{endNum}
